$(document).ready( function() {
    $('#myCarousel').carousel({
		interval:   4000
	});
	
	var clickEvent = false;
	$('#myCarousel').on('click', '.sliddd a', function() {
			clickEvent = true;
			$('.sliddd li').removeClass('active');
			$(this).parent().addClass('active');		
	}).on('slid.bs.carousel', function(e) {
		if(!clickEvent) {
			var count = $('.sliddd').children().length -1;
			var current = $('.sliddd li.active');
			current.removeClass('active').next().addClass('active');
			var id = parseInt(current.data('slide-to'));
			if(count == id) {
				$('.sliddd li').first().addClass('active');	
			}
		}
		clickEvent = false;
	});


	// خاص بعمل ajax عشان الفورم بتاعتي
	$(function () {
		$("#register").ajaxForm ({   /*خاص ب register*/
            beforeSend: function () {
                $("#register_result").html("<img src='images/loading.gif' width='50px'>");
            },success:function (data) {
				$("#register_result").html(data);
            }
        });
    });

    $(function () {
        $("#login").ajaxForm ({    /*خاص ب login*/
            beforeSend: function () {
                $("#login_result").html("<img src='images/loading.gif' width='30px'>");
            },success:function (data) {
                $("#login_result").html(data);
            }
        });
    });

    $(function () {
        $("#request").ajaxForm ({    /*خاص ب request*/
            beforeSend: function () {
                $("#request_result").html("<img src='images/loading.gif' width='30px'>");
            },success:function (data) {
                $("#request_result").html(data);
            }
        });
    });
    
    $(function () {
        $("#comments").ajaxForm ({    /*خاص ب comment*/
            beforeSend: function () {
                $("#com_result").html("<img src='images/loading.gif' width='30px'>");
            },success:function (data) {
                $("#com_result").html(data);
            }
        });
    });
    /////////////////////////////////////////////////////

});